import React from "react";

const skills = [
  { icon: "fab fa-html5", name: "HTML", bg: "bg-1" },
  { icon: "fab fa-css3-alt", name: "CSS", bg: "bg-2" },
  { icon: "fab fa-js", name: "JavaScript", bg: "bg-3" },
  { icon: "fab fa-node-js", name: "JavaScript", bg: "bg-4" },
  { icon: "fab fa-react", name: "React", bg: "bg-5" },
  { icon: "fa fa-database", name: "Mngo DB", bg: "bg-1" },
  { icon: "fa fa-database", name: "Mngo DB", bg: "bg-1" },
  { icon: "fab fa-react", name: "React", bg: "bg-5" },
  { icon: "fa fa-database", name: "Mngo DB", bg: "bg-1" },
  { icon: "fab fa-react", name: "React", bg: "bg-5" },
  { icon: "fa fa-database", name: "Mngo DB", bg: "bg-1" },

  // Add more skills here
];
const Skills = () => {
  // Function to split an array into chunks
  function chunkArray(array, chunkSize) {
    const chunks = [];
    for (let i = 0; i < array.length; i += chunkSize) {
      chunks.push(array.slice(i, i + chunkSize));
    }
    return chunks;
  }

  // Split the skills array into chunks of 3
  const skillChunks = chunkArray(skills, 3);

  return (
    <>
      <section
        data-scroll-index="2"
        className="section experience-section"
        id="skill"
      >
        <div className="container">
          <div className="rows gy-5">
            <div className="col-lg-4">
              <div className="section-heading">
                <h3 className="m-0">
                  <span>My Skills</span>
                </h3>
              </div>
              <div className="skill-box">
                <div className="row g-4">
                  {skillChunks.map((skillChunk, chunkIndex) => (
                    <div className="col-md-4 col-lg-6 col-6 " key={chunkIndex}>
                      {skillChunk.map((skill, index) => (
                        <div className="row" key={index}>
                          <div className="col-lg-6">
                            <div className={`feature-box-02 ${skill.bg}`}>
                              <div className="icon">
                                <i className={skill.icon}></i>
                              </div>
                              <h6>{skill.name}</h6>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Skills;
